import React from "react";
import styles from "../styles/NexusStocks.module.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowUp, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import { GetStocks } from "../js/stocks";
import { Line } from 'react-chartjs-2';

class NexusStocks extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            stocks: []
        };

      
        fetch('/stocks')
        .then(response => response.json())
        .then(data => {
            console.log(data);
            this.setState({ stocks: data });
        })
        .catch(error => console.error('Error:', error));

        this.setStocks = this.setStocks.bind(this);

        var _ = new Promise(async (resolve, reject) => {
            await GetStocks(this.setStocks);
        });
            
    }

    setStocks(stocks) {
        if(stocks.length != undefined){
            this.setState({ stocks: stocks });
        }
        
    }

    renderStocks(){

        const data = {
            labels: this.state.stocks.map(stock => stock.date), // replace 'date' with the actual property name
            datasets: [
                {
                    label: 'Stock Price',
                    data: this.state.stocks.map(stock => stock.price), // replace 'price' with the actual property name
                    fill: false,
                    backgroundColor: 'rgb(75, 192, 192)',
                    borderColor: 'rgba(75, 192, 192, 0.2)',
                },
            ],
        };
    
        return (
            <div>
                <Line data={data} />
                {this.state.stocks.map((stock, index) => {
                    const color = stock.change > 0 ? styles.green : styles.red;
                    const icon  = stock.change > 0 ? faArrowUp : faArrowDown;
                    return (
                        <div key={index} className={`${styles.stockDiv} ${color}`}>
                            <p>{stock.symbol}</p>
                            <p>{parseFloat(stock.price).toFixed(4)}</p>
                            <p>
                                {parseFloat(stock.change).toFixed(4)}
                                <FontAwesomeIcon className={`${styles.iconx}`} icon={icon} />
                            </p>
                        </div>
                    );
                })}
            </div>
        );
    }
    render() {
        return (
            <>
                <div className={styles.mainDiv}>
                    {
                        this.state.stocks.length > 0 ? this.renderStocks() : <div>Loading...</div>
                    }
                </div>
            </>
        );
    }
}

export default NexusStocks;
